function silbido_init% close('all', 'hidden');  % close all figure windows% Add subdirectories to search pathRootDir = fileparts(which(mfilename));if (exist(fullfile(RootDir, 'src', 'matlab'), 'dir'))    % Handle the development structure    paths = genpath2(fullfile(RootDir, 'src', 'matlab'));    addpath(paths);    paths = genpath2(fullfile(RootDir, 'src', 'sandbox'));    addpath(paths);        % Attempt to pick up development paths first    compiledloc = 'eclipse';      switch compiledloc        case 'ant'            % Compiled Java is in RootDir/build/java            if exist(fullfile(RootDir, 'build', 'java'))                javaaddpath(fullfile(RootDir, 'build', 'java'));            end        case 'eclipse'            % Compiled Java is in RootDir/src/java/bin            if exist(fullfile(RootDir, 'src', 'java', 'bin'))                javaaddpath(fullfile(RootDir, 'src', 'java', 'bin'));            end    end        % Jar files are in RootDir/src/java/lib    java_lib_dir = fullfile(RootDir, 'src', 'java', 'lib');    java_archives = utFindFiles({'*.jar'}, {java_lib_dir}, 1);    if ~ isempty(java_archives);        % filter our source jars        sourceP = regexp(java_archives, '-sources.jar');        sourceI = ~cellfun(@isempty, sourceP);        java_archives(sourceI) = [];        % Add what remains        javaaddpath(java_archives);    end        % Ensure we found the java classes.    import tonals.*;    if (~exist('tonals.tonal'))        % perhaps the user is using a source code distribution.  If so        % the silbido jar file will be in build\jar, see if it is there        jerr = classload_err();        silbido_archive = utFindFiles({'*.jar'}, fullfile(RootDir, 'build', 'jar'));        if isempty(silbido_archive)            error(sprintf(...                '%s\nVerify src/java/bin .class files present and have correct compile target.'), jerr);        else            javaaddpath(silbido_archive);            import tonals.*;            if ~ exist('tonals.tonal')                error(sprintf(...                    '%s\nVerify src/java/bin .class or buid/jar files present and have correct compile target.'), jerr);            end        end    endelse    % Handle the distribution structure    paths = genpath2(fullfile(RootDir, 'matlab'));    addpath(paths);        java_dist_dir = fullfile(RootDir, 'java');    java_archives = utFindFiles({'*.jar'}, {java_dist_dir}, 1);    if ~ isempty(java_archives);        javaaddpath(java_archives);    end         % Ensure we found the java classes.    import tonals.*;    if (~exist('tonals.tonal'))        jerr = classloaderr_str();        error(sprintf(...            '%s\nVerify src/java/bin .class files present and have correct compile target.'), jerr);    endendfunction p = genpath2(d, varargin)if nargin==0,  p = genpath(fullfile(matlabroot,'toolbox'));  if length(p) > 1, p(end) = []; end % Remove trailing pathsep  returnend  % initialise variablesmethodsep = '@';  % qualifier for overloaded method directoriesp = '';           % path to be returned% Generate path based on given root directoryfiles = dir(d);if isempty(files)  returnendExcludeDirs{1} = {'.', '..', 'private'};   % always exclude the following% defaultsExcludeRootDir = 0;     % include rootk = 1;while k < length(varargin)  switch varargin{k}   case 'ExcludeRootDir'    ExcludeRootDir = varargin{k+1};     varargin{k+1} = 0;  % Set argument to 0 for recursive calls    k=k+2;   case 'ExcludeDirs'    ExcludeDirs{end+1} = varargin{k+1}; k=k+2;  endendif ~ ExcludeRootDir  % Add d to the path even if it is empty.  p = [p d pathsep];end% set logical vector for subdirectory entries in disdir = logical(cat(1,files.isdir));%% Recursively descend through directories which are neither% private nor "class" directories.%dirs = files(isdir); % select only directory entries from the current listingfor i=1:length(dirs)  dirname = dirs(i).name;    % Assume that dir should be included unless we find otherwise.  Include = 1;  % Don't include object methods   if strncmp(dirname, methodsep, 1)    Include = 0;  end  % Check for other special names that should be excluded  for k=1:length(ExcludeDirs)      if sum(strcmp(dirname, ExcludeDirs{k})) > 0          Include = 0;          break;      end  end  if Include    p = [p genpath2(fullfile(d, dirname), varargin{:})];  endend%------------------------------------------------------------------------------function str = classload_err()% str = classload_err()% Generate a string describing possible problems with class loadingstr = sprintf(...    ['Unable to load Java classes.\n', ...     'Matlab is running %s\n', ...    'Java classes must be compiled with this version or earlier.'], ...    version('-java'));